#!/usr/bin/env node
var program = require('commander');
var read_files = require('./readFiles.js');

program
  .arguments('<originPath>')
  .arguments('<destPath>')
  .action(function(originPath, destPath) {
    console.log('originPath: %s destPath: %s',
        originPath, destPath);
    read_files.readFiles(originPath,destPath);
  })
  .parse(process.argv);
